package add2num;

public class add2num {

	public static void main(String[] args) {
		int num1 = 15;
		int num2 = 20;
		add(num1, num2);
	}
	public static void add(int num1, int num2) {
		int sum = num1 + num2;
		System.out.println(num1 + "+" + num2 + "=" + sum);
		// TODO Auto-generated method stub

	}
	
}
